package RSA;

import java.security.interfaces.RSAPrivateKey;
import java.security.interfaces.RSAPublicKey;

import static RSA.RSAUtils.decryptByPrivateKey;
import static RSA.RSAUtils.encryptByPublicKey;

public class getKey {
    public static RSAPublicKey pubkey3 = RSAUtils.getPublicKey("", "65537");
    public static RSAPrivateKey prikey3 =RSAUtils.getPrivateKey("", "");

    public static void main(String[] args) throws Exception {
        pubkey3 = RSAUtils.getPublicKey("", "65537");
        prikey3 = RSAUtils.getPrivateKey("", "");
        //明文
        String ming = "/u4f5";

        String mi = encryptByPublicKey(ming, pubkey3);
        System.out.println("密文："+mi);

        long time = System.currentTimeMillis();
        //解密后的明文
        ming = decryptByPrivateKey(mi, prikey3);
        System.out.println("明文"+ming);
    }
}
